    public void Spinner(int seconds)
    {
        DateTime startTime = DateTime.Now;
        DateTime endTime = startTime.AddSeconds(seconds);
        List<string> spinner = new List<string>();
        spinner.Add("|");
        spinner.Add("/"); 
        spinner.Add("-"); 
        spinner.Add("\\"); 
        spinner.Add("|"); 
        spinner.Add("/");
        spinner.Add("-");
        spinner.Add("\\");

        int i = 0;
        
        while (DateTime.Now < endTime)
        {
            string s = spinner[i];
            Console.Write(s);
            Thread.Sleep(100); //1000
            Console.Write("\b \b");
            
            i++;
            if(i >= spinner.Count)
            {
                i = 0;
            }
        }
        Console.WriteLine(); 
    }

